mapper.select:
        select count(1)
        from t_moment_like
		where is_delete = 0
		and subject_id = #{subjectId}
		and create_by = #{createBy}

mapper.update:
update t_blog
set like_num = ifnull(like_num,0) + #{num}
where id = #{subjectId}

update t_moment_comment
set like_num = ifnull(like_num,0) + #{num}
where id = #{subjectId}

